import { useState } from "react";
import {
  HomeIcon,
  UserIcon,
  Cog6ToothIcon,
  ArrowLeftOnRectangleIcon,
  Bars3Icon,
  MoonIcon,
  SunIcon,
} from "@heroicons/react/24/outline";

export default function Dashboard() {
  const [active, setActive] = useState("Home");
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [darkMode, setDarkMode] = useState(false);
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const menuItems = [
    { name: "Home", icon: HomeIcon },
    { name: "Profile", icon: UserIcon },
    { name: "Settings", icon: Cog6ToothIcon },
  ];

  return (
    <div className={`${darkMode ? "dark" : ""} flex min-h-screen`}>
      {/* Sidebar */}
      <aside
        className={`${
          sidebarOpen ? "w-64" : "w-20"
        } bg-gray-900 text-white flex flex-col transition-all duration-300`}
      >
        <div className="flex items-center justify-between p-4">
          <h2 className={`${sidebarOpen ? "block" : "hidden"} font-bold`}>
            My App
          </h2>
          <button
            onClick={() => setSidebarOpen(!sidebarOpen)}
            className="p-2 rounded hover:bg-gray-800"
          >
            <Bars3Icon className="h-6 w-6" />
          </button>
        </div>

        <nav className="flex-1 space-y-2 mt-6">
          {menuItems.map((item) => {
            const Icon = item.icon;
            return (
              <button
                key={item.name}
                onClick={() => setActive(item.name)}
                className={`flex items-center gap-3 w-full px-3 py-2 rounded-lg transition ${
                  active === item.name ? "bg-gray-700" : "hover:bg-gray-800"
                }`}
              >
                <Icon className="h-6 w-6" />
                {sidebarOpen && <span>{item.name}</span>}
              </button>
            );
          })}
        </nav>

        <button className="flex items-center gap-3 px-3 py-2 rounded-lg hover:bg-red-600 transition m-4">
          <ArrowLeftOnRectangleIcon className="h-6 w-6" />
          {sidebarOpen && <span>Logout</span>}
        </button>
      </aside>

      {/* Main Content */}
      <div className="flex-1 bg-gray-100 dark:bg-gray-800 p-6">
        {/* Navbar */}
        <header className="flex justify-between items-center mb-6">
          <div className="flex items-center gap-4">
            <button
              onClick={() => setDarkMode(!darkMode)}
              className="p-2 rounded-md bg-gray-200 dark:bg-gray-700"
            >
              {darkMode ? (
                <SunIcon className="h-6 w-6" />
              ) : (
                <MoonIcon className="h-6 w-6" />
              )}
            </button>
          </div>

          {/* User Dropdown */}
          <div className="relative">
            <button
              onClick={() => setDropdownOpen(!dropdownOpen)}
              className="px-4 py-2 bg-gray-200 dark:bg-gray-700 rounded-md"
            >
              User
            </button>
            {dropdownOpen && (
              <div className="absolute right-0 mt-2 w-40 bg-white dark:bg-gray-700 shadow rounded-md">
                <button className="block w-full text-left px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600">
                  Profile
                </button>
                <button className="block w-full text-left px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600">
                  Settings
                </button>
                <button className="block w-full text-left px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600">
                  Logout
                </button>
              </div>
            )}
          </div>
        </header>

        <h1 className="text-2xl font-bold text-gray-900 dark:text-gray-100">
          {active}
        </h1>
        <p className="mt-4 text-gray-600 dark:text-gray-300">
          This is the {active} page content.
        </p>
      </div>
    </div>
  );
}
