import { useState } from "react";
import {
  HomeIcon,
  UserGroupIcon,
  Cog6ToothIcon,
  ShoppingBagIcon,
  ChartBarIcon,
  ArrowRightOnRectangleIcon,
  Bars3Icon,
  EyeDropperIcon,
} from "@heroicons/react/24/outline";

export default function DonationsDashboard() {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [active, setActive] = useState("Blood Donations");
  const [donations, setDonations] = useState([
    {
      id: 1,
      date: "2025-08-15",
      donor: "Juan Dela Cruz",
      bloodGroup: "A+",
      volume: 500,
    },
    {
      id: 2,
      date: "2025-08-18",
      donor: "Maria Santos",
      bloodGroup: "O-",
      volume: 450,
    },
  ]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedDonation, setSelectedDonation] = useState(null);

  const menuItems = [
    { name: "Dashboard", icon: HomeIcon },
    { name: "Donors", icon: UserGroupIcon },
    { name: "Blood Donations", icon: EyeDropperIcon },
    { name: "Requests", icon: ShoppingBagIcon },
    { name: "Handed Over", icon: ChartBarIcon },
    { name: "Users", icon: UserGroupIcon },
    { name: "Settings", icon: Cog6ToothIcon },
    { name: "Logout", icon: ArrowRightOnRectangleIcon },
  ];

  const handleEdit = (donation) => {
    setSelectedDonation(donation);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedDonation(null);
  };

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900">
      {/* Top Navbar */}
      <nav className="fixed top-0 z-50 w-full bg-[#800000] text-white shadow-md">
        <div className="px-4 py-3 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <button
              onClick={() => setSidebarOpen(!sidebarOpen)}
              className="p-2 rounded-lg hover:bg-[#660000] transition"
            >
              <Bars3Icon className="w-6 h-6 text-white" />
            </button>
            <span className="text-xl font-bold tracking-wide">
              BloodTrack
            </span>
          </div>
          <div className="flex items-center gap-3">
            <img
              src="https://flowbite.com/docs/images/people/profile-picture-5.jpg"
              alt="User"
              className="w-9 h-9 rounded-full border-2 border-white shadow"
            />
          </div>
        </div>
      </nav>

      {/* Sidebar */}
      <aside
        className={`fixed top-0 left-0 z-40 w-64 h-screen pt-20 transform bg-[#800000] text-white shadow-lg transition-transform duration-300 ${
          sidebarOpen ? "translate-x-0" : "-translate-x-full"
        }`}
      >
        <div className="h-full px-3 pb-4 overflow-y-auto">
          <ul className="space-y-1 font-medium">
            {menuItems.map((item) => {
              const Icon = item.icon;
              const isActive = active === item.name;
              return (
                <li key={item.name}>
                  <button
                    onClick={() => setActive(item.name)}
                    className={`flex items-center w-full p-2 rounded-lg transition ${
                      isActive
                        ? "bg-white text-[#800000] font-semibold shadow"
                        : "hover:bg-[#660000]"
                    }`}
                  >
                    <Icon
                      className={`w-6 h-6 ${
                        isActive ? "text-[#800000]" : "text-white"
                      }`}
                    />
                    <span className="ms-3">{item.name}</span>
                  </button>
                </li>
              );
            })}
          </ul>
        </div>
      </aside>

      {/* Main Content */}
      <main
        className={`p-6 mt-16 transition-all duration-300 ${
          sidebarOpen ? "sm:ml-64" : "sm:ml-0"
        }`}
      >
        <div className="p-6 bg-white dark:bg-gray-800 rounded-xl shadow-md">
          {/* Donations Table */}
          <div className="bg-white shadow rounded-lg overflow-hidden">
            <div className="flex items-center justify-between px-6 py-4 border-b">
              <h2 className="text-lg font-semibold text-gray-700">
                List of Donations
              </h2>
              <button className="bg-blue-600 hover:bg-blue-700 text-white text-sm px-4 py-2 rounded-md shadow">
                + New Entry
              </button>
            </div>
            <div className="overflow-x-auto">
              <table className="min-w-full border-collapse">
                <thead className="bg-gray-100">
                  <tr>
                    <th className="border px-4 py-2 text-center text-sm font-medium text-gray-600">#</th>
                    <th className="border px-4 py-2 text-sm font-medium text-gray-600">Date</th>
                    <th className="border px-4 py-2 text-sm font-medium text-gray-600">Donor</th>
                    <th className="border px-4 py-2 text-sm font-medium text-gray-600">Blood Group</th>
                    <th className="border px-4 py-2 text-sm font-medium text-gray-600">Volume (ml)</th>
                    <th className="border px-4 py-2 text-center text-sm font-medium text-gray-600">Action</th>
                  </tr>
                </thead>
                <tbody>
                  {donations.map((donation, index) => (
                    <tr key={donation.id} className="hover:bg-gray-50">
                      <td className="border px-4 py-2 text-center text-sm">{index + 1}</td>
                      <td className="border px-4 py-2 text-sm">
                        {new Date(donation.date).toLocaleDateString("en-US", {
                          month: "short",
                          day: "numeric",
                          year: "numeric",
                        })}
                      </td>
                      <td className="border px-4 py-2 text-sm font-semibold">{donation.donor}</td>
                      <td className="border px-4 py-2 text-sm font-semibold">{donation.bloodGroup}</td>
                      <td className="border px-4 py-2 text-sm font-semibold">{donation.volume}</td>
                      <td className="border px-4 py-2 text-center space-x-2">
                        <button
                          onClick={() => handleEdit(donation)}
                          className="px-3 py-1 text-sm border border-blue-500 text-blue-600 rounded hover:bg-blue-50"
                        >
                          Edit
                        </button>
                        <button className="px-3 py-1 text-sm border border-red-500 text-red-600 rounded hover:bg-red-50">
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </main>

      {/* Modal */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-lg w-full max-w-md p-6">
            <h3 className="text-lg font-semibold mb-4">Edit Donation</h3>
            <form className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700">Date</label>
                <input
                  type="date"
                  defaultValue={selectedDonation?.date}
                  className="mt-1 block w-full border rounded-md px-3 py-2 text-sm"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Donor</label>
                <input
                  type="text"
                  defaultValue={selectedDonation?.donor}
                  className="mt-1 block w-full border rounded-md px-3 py-2 text-sm"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Blood Group</label>
                <input
                  type="text"
                  defaultValue={selectedDonation?.bloodGroup}
                  className="mt-1 block w-full border rounded-md px-3 py-2 text-sm"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Volume (ml)</label>
                <input
                  type="number"
                  defaultValue={selectedDonation?.volume}
                  className="mt-1 block w-full border rounded-md px-3 py-2 text-sm"
                />
              </div>
              <div className="flex justify-end space-x-2 mt-4">
                <button
                  type="button"
                  onClick={closeModal}
                  className="px-4 py-2 bg-gray-200 text-gray-700 rounded hover:bg-gray-300"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                >
                  Save
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}
