import { useState } from "react";
import { Home, User, Settings, LogOut, Menu } from "lucide-react";

export default function Dashboard() {
  const [active, setActive] = useState("Home");
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [darkMode, setDarkMode] = useState(false);
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const menuItems = [
    { name: "Home", icon: <Home size={20} /> },
    { name: "Profile", icon: <User size={20} /> },
    { name: "Settings", icon: <Settings size={20} /> },
  ];

  return (
    <div className={`${darkMode ? "dark" : ""} flex min-h-screen`}>
      {/* Sidebar */}
      {sidebarOpen && (
        <aside className="w-64 bg-gray-900 text-white flex flex-col p-4 transition-all">
          <h2 className="text-xl font-bold mb-6">My App</h2>
          <nav className="flex-1 space-y-2">
            {menuItems.map((item) => (
              <button
                key={item.name}
                onClick={() => setActive(item.name)}
                className={`flex items-center gap-3 w-full px-3 py-2 rounded-lg transition
                  ${active === item.name ? "bg-gray-700" : "hover:bg-gray-800"}`}
              >
                {item.icon}
                <span>{item.name}</span>
              </button>
            ))}
          </nav>

          {/* Logout */}
          <button className="flex items-center gap-3 px-3 py-2 rounded-lg hover:bg-red-600 transition">
            <LogOut size={20} />
            <span>Logout</span>
          </button>
        </aside>
      )}

      {/* Main Content */}
      <div className="flex-1 bg-gray-100 dark:bg-gray-800 p-6">
        {/* Navbar */}
        <header className="flex justify-between items-center mb-6">
          <button
            onClick={() => setSidebarOpen(!sidebarOpen)}
            className="p-2 rounded-md bg-gray-200 dark:bg-gray-700"
          >
            <Menu />
          </button>

          <div className="flex items-center gap-4">
            <button
              onClick={() => setDarkMode(!darkMode)}
              className="p-2 rounded-md bg-gray-200 dark:bg-gray-700"
            >
              {darkMode ? "☀️" : "🌙"}
            </button>

            {/* User Dropdown */}
            <div className="relative">
              <button
                onClick={() => setDropdownOpen(!dropdownOpen)}
                className="px-4 py-2 bg-gray-200 dark:bg-gray-700 rounded-md"
              >
                User
              </button>
              {dropdownOpen && (
                <div className="absolute right-0 mt-2 w-40 bg-white dark:bg-gray-700 shadow rounded-md">
                  <button className="block w-full text-left px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600">
                    Profile
                  </button>
                  <button className="block w-full text-left px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600">
                    Settings
                  </button>
                  <button className="block w-full text-left px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600">
                    Logout
                  </button>
                </div>
              )}
            </div>
          </div>
        </header>

        <h1 className="text-2xl font-bold text-gray-900 dark:text-gray-100">{active}</h1>
        <p className="mt-4 text-gray-600 dark:text-gray-300">
          This is the {active} page content.
        </p>
      </div>
    </div>
  );
}
